#include <iostream> using namespace std; // A variable declaration like basic data types
struct Point
{ int x, y;
}; void init_point(struct Point &p);
void print_point(const struct Point &p);
void modify_point(struct Point &p); int main()
{ struct Point p1; // The variable p1 is declared like a normal variable init_point(p1); print_point(p1); modify_point(p1); print_point(p1); return 0;
} void init_point(struct Point &p)
{ p.x = 10; p.y = 20;
} void print_point(const struct Point &p)
{ std::cout << "(" << p.x << ", " << p.y << ")" << std::endl;
} void modify_point(struct Point &p)
{ p.x = 100; p.y = 200;
} Output (in console): $ ./a.out
(10, 20)
